import React, { Component } from 'react'
import {
  Circle,
  FeatureGroup,
  LayerGroup,
  Map,
  Popup,
  Rectangle,
  TileLayer,
  Polygon,
  CircleMarker,
  Tooltip
} from 'react-leaflet'
import Typography from '@material-ui/core/Typography';


import data from "./arWorks.json";
const center = [51.505, -0.09]
const rectangle = [
  [51.49, -0.08],
  [51.5, -0.06],
]


export default class OtherLayersExample extends Component {
    constructor(props) {
        super(props);
        this.state = {
            lat: 46.0,
            lng: 25.096306,
            zoom: 7,
            draggable: false,
            day: props.day,
            width: 0, height: 0
        };
        this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
    }

    static getDerivedStateFromProps(props, state) {
      if (props.day !== state.day) {
        return {
          day: props.day
        }
      }
      return null;
    }

    map;

    componentDidMount() {
      this.updateWindowDimensions();
      window.addEventListener('resize', this.updateWindowDimensions);
    }
    
    componentWillUnmount() {
      window.removeEventListener('resize', this.updateWindowDimensions);
    }
    
    updateWindowDimensions() {
      this.setState({ width: window.innerWidth, height: window.innerHeight });
    }

    getRandomColor = () => {
      var letters = '0123456789ABCDEF';
      var color = '#';
      for (var i = 0; i < 6; i++) {
        color += letters[Math.floor(Math.random() * 16)];
      }
      return color;
    }
  //  drawCities = () => {
  //    let arrCities = [];
  //     for(let city in data.cities) {
  //       let item = data.cities[city];
  //       arrCities.push(item);
  //     }
  //     arrCities.map((city, i ) => {
  //         return <Polygon
  //         positions={city.geometry.coordinates}
  //         color="red"
  //         >
  //           <Tooltip>TOOLTIP</Tooltip>
  //         </Polygon>
  //     });
  //  }

    getMapZoom = () => {
      return this.map && this.map.leafletElement.getZoom();
    }
    enableDragging = () => {
      if (this.getMapZoom() < 8) {
          this.setState({draggable: false});
          return;
      }
      this.setState({draggable: true});
    };

  render() {
    const position = [this.state.lat, this.state.lng];
    let arrCities = [];
    let arrCityNames = [];
    if (this.getMapZoom() > 8) {
      for(let city in data.cities) {
        let item = data.cities[city];
        arrCities.push(item);
        console.log(item);
        arrCityNames.push(city);
      }
    }
        return (


      <Map style={{width: this.state.width, height : this.state.height}}
      ref = {(ref) => {this.map = ref}}
      center={position}
      zoom={this.state.zoom}
      dragging={this.state.draggable}
      minZoom={7}
      maxZoom={10}
      onzoomend={this.enableDragging}
      >>
          {
             arrCities.map((city, i ) => {
              return <Polygon
              key={i}
              positions={city.geometry.coordinates}
              color={this.getRandomColor()}
              >
                <Tooltip>
                  {city.natLevName}: {arrCityNames[i]}
                  <br/>
                  Populatie: {city.population}
                </Tooltip>
              </Polygon>
          })
          }
                <Polygon
        positions={data.geometry.coordinates}
        color="yellow"
        fillColor="black"
      >
         <Tooltip>
           Judet: {data.name}
           <br/>
          Populatie: {data.population}
         </Tooltip>
      </Polygon>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <FeatureGroup color="purple">
          <Popup>Popup in FeatureGroup</Popup>
        </FeatureGroup>
      </Map>

    )
  }
}
